const form = document.querySelector('au-schema-form');
form.schema = {
  type: 'object',
  properties: {
    name: { type: 'string', title: 'Name', minLength: 2 },
    email: { type: 'string', format: 'email', title: 'Email' },
    role: { type: 'string', enum: ['user', 'admin'], title: 'Role' }
  },
  required: ['name', 'email']
};

const list = document.querySelector('au-virtual-list');
list.setItems(Array.from({length: 10000}, (_, i) => ({id: i, text: 'Item ' + i})));
list.setRenderFn(item => '<div>' + item.text + '</div>');
